property.value:
/** Output sensor data.
 *  FIXME: explain the sensor data.
 *
 * If there is more than one instance of this actor, then all instances
 * will share the same global sensor data array, and will also share the
 * same distance and angle records. It is up to the user to ensure that
 * no two such instances execute simultaneously.
 *  E.g.,
 *    - distance: An integer giving the total accumulated travel distance.
 *    - angle: 
 */   
/***preinitBlock***/
#ifndef SENSOR_ACTOR
#define SENSOR_ACTOR
volatile int16_t distance = 0;
volatile int16_t angle = 0;
volatile uint8_t sensors[Sen0Size];
#endif
/**/

/***initBlock***/
  // Set the sensor data to be all zero.
  // This initializes the buffer that gets
  // filled by the interrupt service routine that
  // reads from the serial port.
  for(int i = 0; i < Sen6Size; i++) {
     sensors[i] = 0x0;
  }
/**/

/***fireBlock***/
if ($ref(trigger)) {
  // Request Sensors Packet 2
  byteTx(CmdSensors);
  // Request packet 0, which has 26 bytes of information.
  byteTx(0);

  for(int i = 0; i < Sen0Size; i++) {
    sensors[i] = byteRx();
  }

  // Output sensor data.
  $ref(bumpLeft) = sensors[SenBumpDrop] & BumpLeft;
  $ref(bumpRight) = sensors[SenBumpDrop] & BumpRight;
  $ref(wheelDropFront) = sensors[SenBumpDrop] & WheelDropFront;            
  $ref(wheelDropLeft) = sensors[SenBumpDrop] & WheelDropLeft;            
  $ref(wheelDropRight) = sensors[SenBumpDrop] & WheelDropRight;            
  $ref(wall) = sensors[SenWall];
  $ref(cliffLeft) = sensors[SenCliffL];
  $ref(cliffFrontLeft) = sensors[SenCliffFL];
  $ref(cliffFrontRight) = sensors[SenCliffFR];
  $ref(cliffRight) = sensors[SenCliffR];
  $ref(virtualWall) = sensors[SenVWall];
  $ref(advanceButton) = sensors[SenButton] & ButtonAdvance;
  $ref(playButton) = sensors[SenButton] & ButtonPlay;
  // Update accumulated distance and angle.
  distance += (int)((sensors[SenDist1] << 8) | sensors[SenDist0]);
  $ref(distance) = distance;
  angle += (int)((sensors[SenAng1] << 8) | sensors[SenAng0]);
  $ref(angle) = angle;
/* FIXME: Skipped the battery state
#define SenChargeState  16
#define SenVolt1        17
#define SenVolt0        18
#define SenCurr1        19
#define SenCurr0        20
#define SenTemp         21
#define SenCharge1      22
#define SenCharge0      23
#define SenCap1         24
#define SenCap0         25
*/

}

/**/

/***wrapupBlock***/
/**/

/***preinitBlock***/
/**/

/***initBlock***/
  // Set up the ADC on pin C5
  DIDR0 |= 0x20;  // disable digital input on C5
  PRR &= ~_BV(PRADC); // Turn off ADC power save
  ADCSRA = (_BV(ADEN) | _BV(ADPS2) | _BV(ADPS1) | _BV(ADPS0)); // Enabled, prescaler = 128
  ADMUX = (_BV(REFS0) | 0x07); // set voltage reference, select channel C7 (was C5, modified by Winthrop)
  // First channel read by ADC will be the one specified on the line above.
/**/

/***fireBlock***/
      ADCSRA |= 0x40;
      while(ADCSRA & 0x40) ;
      // The ADC output is 10 bits unsigned, or 0 to 1023.
      // Normalize here.
      // FIXME: Why reverse the polarity?
      $ref(left) = $val(DCoffset) + 512 - ADC;
      ADMUX = (_BV(REFS0) | 0x06);  // Change ADC mux to read other channel
      // Sample the Y accelerometer, pointed 45 degrees right of forward, on channel 6
      ADCSRA |= 0x40;
      while(ADCSRA & 0x40) ;
      $ref(right) = $val(DCoffset) + 512 - ADC;
      ADMUX = (_BV(REFS0) | 0x07);  // Change ADC mux back to read first channel
/**/

/***wrapupBlock***/
/**/

/** Send a command to the iRobot Create to drive at
 *  the specified velocity, in mm per second, and at the
 *  specified angle, in mm.  The velocity can range from
 *  -500 to 500, and the radius from -2000 to 2000.

FIXME: Use portParameters.
FIXME: ExpertParameter STRAIGHT, etc.
FIXME: Details from the manual below:

The longer radii make Create drive straighter, while
the shorter radii make Create turn more. The radius is
measured from the center of the turning circle to the center
of Create. A Drive command with a positive velocity and a
positive radius makes Create drive forward while turning
toward the left. A negative radius makes Create turn toward
the right. Special cases for the radius make Create turn
in place or drive straight, as specified below. A negative
velocity makes Create drive backward.
NOTE: Internal and environmental restrictions may prevent
Create from accurately carrying out some drive commands.
For example, it may not be possible for Create to drive at
full speed in an arc with a large radius of curvature.
? Serial sequence: [137] [Velocity high byte] [Velocity low byte]
[Radius high byte] [Radius low byte]
? Available in modes: Safe or Full
? Changes mode to: No Change
? Drive data byte 1: Velocity (-500 ? 500 mm/s)
? Drive data byte 2: Radius (-2000 ? 2000 mm)
Special cases:
Straight = 32768 or 32767 = hex 8000 or 7FFF
Turn in place clockwise = hex FFFF
Turn in place counter-clockwise = hex 0001

 */

/***preinitBlock***/
// Send Create drive commands in terms of velocity and radius
#ifndef _DRIVE_DRIVE
#define _DRIVE_DRIVE

// Global variables
volatile uint16_t timer_cnt = 0;
volatile uint8_t timer_on = 0;
volatile uint8_t sensors_flag = 0;
volatile uint8_t sensors_index = 0;
volatile uint8_t sensors_in[Sen6Size];
volatile uint8_t sensors[Sen6Size];
volatile int16_t distance = 0;
volatile int16_t angle = 0;

void drive(int16_t velocity, int16_t radius)
{
  byteTx(CmdDrive);
  byteTx((uint8_t)((velocity >> 8) & 0x00FF));
  byteTx((uint8_t)(velocity & 0x00FF));
  byteTx((uint8_t)((radius >> 8) & 0x00FF));
  byteTx((uint8_t)(radius & 0x00FF));
}

// Delay for the specified time in ms and update sensor values
void delayAndUpdateSensors(uint16_t time_ms)
{
  uint8_t temp;

  timer_on = 1;
  timer_cnt = time_ms;
  while(timer_on)
  {
    if(!sensors_flag)
    {
      for(temp = 0; temp < Sen6Size; temp++)
        sensors[temp] = sensors_in[temp];

      // Update running totals of distance and angle
      distance += (int)((sensors[SenDist1] << 8) | sensors[SenDist0]);
      angle += (int)((sensors[SenAng1] << 8) | sensors[SenAng0]);

      byteTx(CmdSensors);
      byteTx(6);
      sensors_index = 0;
      sensors_flag = 1;
    }
  }
}

#endif
/**/

/***initBlock***/
  // As a precaution, stop driving.
  // RadStraight = 32768, the code for no turns.
  drive(0, RadStraight);
/**/

/***fireBlock***/

  drive($ref(velocity), $ref(radius));
  while(-(angle) <= $ref(RotateAngle)) {
    // wait a little more than one robot tick for sensors to update
    delayAndUpdateSensors(20);
  }

  drive(0, RadStraight);
  delayAndUpdateSensors(20);
  angle=0;

  $ref(done) = true;

/**/

/***wrapupBlock***/
/**/

