  drive(0, RadStraight);
/**/

/***fireBlock***/
  drive($ref(velocity), $ref(radius));

  if($ref(velocity)==1){
    while(distance <= $ref(UnitDistance))
    delayAndUpdateSensors(20);
  } else if($ref(velocity)==1.414){
    while(distance <= $ref(UnitDistance) * 1.414)
    delayAndUpdateSensors(20);
  }

  drive(0, RadStraight);
  delayAndUpdateSensors(20);
  distance = 0;


  $ref(done) = true;
